import React, { useState } from 'react';
import styles from './css/styles.module.css';
import {
    getDocumentType,
    toggleHideShow,
    getImageType,
    deleteImage
} from './functions';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faCaretDown, faCaretUp, faPlusCircle, faMinusCircle, faReply } from '@fortawesome/free-solid-svg-icons';
import logo from './../../../src/me.png';



export const DisplayDocument = (props) => {

    const documentDataDefault = {
        date: "1,Jun.2021",
        MimeType: "JPG",
        src: logo,

    }
    const documentIdTypes = [];
    documentIdTypes.push(<option key={'Select'} value={'Select'}>Select</option>);
    documentIdTypes.push(<option key={'Govt Issued Id (Front)'} value={'Govt Issued Id (Front)'}>Govt. Issued Id (Front)</option>);
    documentIdTypes.push(<option key={'Govt Issued Id (Back)'} value={'Govt Issued Id (Back)'}>Govt. Issued Id (Back)</option>);
    documentIdTypes.push(<option key={'Social Security Card'} value={'Social Security Card'}>Social Security Card</option>);
    documentIdTypes.push(<option key={'Other'} value={'Other'}>Other</option>)

    const imageType =[];
    imageType.push(<option key={'image1'} value={'Image 1'}>Image 1</option>);
    imageType.push(<option key={'image2'} value={'Image 2'}>Image 2</option>)

    const [documentData, setDocumentData] = useState(documentDataDefault);
    const [toggle, setToggle] = useState(true);
    const [zoom, setZoom] = useState(100);
    const [rotation, setRotation] = useState(0);
    return (

        <>
            <div style={{ height: (toggle ? '13vh' : '45.523vh') }} className={styles['document-image-outer-container']}>
                <div className={styles['player-document-info-players-document-label-container']}>
                    <div className={styles['player-document-info-players-document-text']}>Player's Documents</div>
                    <div className={styles['player-document-info-players-document-selection-text']}>Selection</div>
                    <select style={{ fontSize: '1.5em',height: (toggle ? '82.25%' : '42.25%') }}
                        className={styles['player-document-info-players-document-selection']}
                        defaultValue={imageType[0]} onChange={(e) => getImageType(e)}   >
                        {imageType}
                    </select>
                </div>
                <div className={`${styles['player-document-info-players-document-details-labels-container']}`}>
                    <div className={`${styles['player-document-info-players-document-details-labels-text']} ${styles['player-document-info-players-document-details-labels-text-upload-date']}`} aria-label='Upload Date' tabIndex='0'>Upload Date</div>
                    <div className={styles['player-document-info-players-document-details-labels-text']} aria-label='File Type' tabIndex='0'>File Type</div>
                    <div className={styles['player-document-info-players-document-details-labels-text']} aria-label='Document Type' tabIndex='0'>Document Type</div>
                </div>
                <div style={{ width: '91.5%', height: '.7em', marginBottom: '.7em' }}>
                    <hr className={styles['document-image-outer-container-hr']} />
                </div>
                <div style={{ height: (toggle ? '27.124%' : '19.124%') }}
                    className={`${styles['player-document-info-players-document-details-content-container']}`}>
                    <div className={`${styles['player-document-info-players-document-details-content-text']} ${styles['player-document-info-players-document-details-content-text-upload-date']}`} tabIndex='0'>{documentData.date}</div>
                    <div className={styles['player-document-info-players-document-details-content-text']} tabIndex='0'>{documentData.MimeType}</div>

                    <select style={{ height: (toggle ? '100%' : '80%') }}
                        className={styles['player-document-info-players-document-details-content-select']}
                        defaultValue={documentIdTypes[0]} onChange={(e) => getDocumentType(e)}   >
                        {documentIdTypes}
                    </select>
                    <div onClick={(e) => deleteImage("")} 
                    style={{ height: (toggle ? `100%` : '70%') }} 
                    className={styles['player-document-info-players-document-details-content-delete']}>
                        <FontAwesomeIcon icon={faTrash} />
                    </div>
                    <div style={{ height: (toggle ? '100%' : '70%') }} onClick={(e) => toggleHideShow(setToggle, toggle,setDocumentData)} className={styles['player-document-info-players-document-details-content-button-hide-show']}>
                        <div>{toggle ? 'Hide' : 'Show'}</div>
                        {toggle ? <FontAwesomeIcon icon={faCaretUp} /> : <FontAwesomeIcon icon={faCaretDown} />}
                    </div>
                </div>

                {!toggle && <><div className={styles['document-image-container']}>
                    <div
                        style={
                            {
                                backgroundSize: ` ${zoom === 100 ? 'cover': `${zoom}%`}`,
                                backgroundImage: `url(${documentData.src})`,
                                backgroundRepeat: 'no-repeat',
                                backgroundPosition: 'center',
                            }
                        }
                        className={`${styles['document-image-src-container']}                         
                    ${rotation === 90 ? styles['rotateimg90'] :
                                rotation === 180 ? styles['rotateimg180'] :
                                    rotation === 270 ? styles['rotateimg270'] :
                                        styles['rotateimg0']
                            }`} alt={'image_name'} >
                    </div>
                    <div className={styles['document-image-src-controll-container']}>
                        <FontAwesomeIcon onClick={(e) => setRotation((rotation + 90) % 360)} icon={faReply} />
                        <div>
                            <FontAwesomeIcon onClick={(e) => { setZoom((zoom + 10)) }} style={{ marginRight: '.2em' }} icon={faPlusCircle} />
                            <FontAwesomeIcon onClick={(e) => setZoom((zoom - 10))} icon={faMinusCircle} />
                        </div>
                    </div>
                </div></>}

            </div>
        </>
    );

}
