            infrastructureOptions, upTime, queryStr, nonWinner, ticketScanVelocity
        } = this.state;

        return (
            <div className='system-dashboard-container'>
                <div className='system-dashboard-widget-container'>
                    <div className='c1r1'>
                        <Widget
                            value={winner}
