const axios = require('axios');
const data = require('./__test__/test_data.json');

const makeHttpCall = async (options) => {

    console.log(">>>>>>>>>>>>>>> makeHttpCall"); 
    return data;
    try {
        let source ={
            headers: {'X-API-Key': process.env.REACT_APP_BACK_END_GET_DOCUMENT_IMAGE_X_API_KEY}
        }
        options = Object.assign(options, source)
        let response = await axios(options);
        return  response.data;
    }
    catch (e) {
        throw new Error(e);
    }
}

export const getData = async ( playerId , setDocumentData ) => {
    console.log(">>>>>>>>>>>>>>> getData");
    const options =
    {
        method: 'get',
        url: process.env.REACT_APP_BACK_END_GET_DOCUMENT_IMAGE+'/document/'+ playerId
    }

    try
    {
        
        const data = await makeHttpCall(options);
        
        if(data.message !== undefined)
        {
            alert(`Player ${playerId} not fount`);
            return;
        }        
        setDocumentData(data);              
    } 
    catch(e){
        alert(e);
    }

}
export  const getImageType = ( e , setIndex ) =>{
    console.log(">>>>>>>>>>>>>>> getImageType");

    setIndex(e.target.value.split(" ")[1]);

}
export const getDocumentType = async (event, setDocumentType,uploadId,documentData, setDocumentData) => {
    console.log(">>>>>>>>>>>>>>> getDocumentType");
    
    const options =
    {
        method: 'put',
        url: process.env.REACT_APP_BACK_END_GET_DOCUMENT_IMAGE+'/document/'+ uploadId,
        data: {
             type: event.target.value
          }
    }

    try
    {
        const data = await makeHttpCall(options);
        
        setDocumentData(documentData.map(element => {
            if(element.uploadId === uploadId)
                element.documentType = event.target.value;   
            return element;
        }));

        setDocumentType(event.target.value);       
                     
    } 
    catch(e){
        alert(e);
    }

}

export const toggleHideShow = async ( setToggle, toggle) => {
    console.log(">>>>>>>>>>>>>>> toggleHideShow");
    setToggle(!toggle);
    
}

export const deleteImage = (uploadId) => {

    console.log(">>>>>>>>>>>>>>> deleteImage");
    if(uploadId === "")
    {
        alert('Upload id is empty');
        return;
    }
    const options =
    {
        method: 'delete',
        url: process.env.REACT_APP_BACK_END_GET_DOCUMENT_IMAGE+'/document/'+ uploadId
    }

    try
    { 
       alert(makeHttpCall(options).message);
    }catch(e){
        alert(e);
    }

}
