const axios = require('axios');


const makeHttpCall = async (options) => {

    try {
        let source ={
            headers: {'X-API-Key': process.env.REACT_APP_BACK_END_GET_DOCUMENT_IMAGE_X_API_KEY}
        }
        options = Object.assign(options, source)
        let response = await axios(options);
        return  response.data;
    }
    catch (e) {
        throw new Error(e);
    }
}

export const getImageType = (event) => {
    console.log(event.target.value);

}

export const getDocumentType = (event) => {
    console.log(event.target.value);

}

export const toggleHideShow = async ( setToggle, toggle, playerId, setDocumentData) => {
   
    setToggle(!toggle);
    const options =
    {
        method: 'get',
        url: process.env.REACT_APP_BACK_END_GET_DOCUMENT_IMAGE+'/document/'+ playerId
    }
    try{
        const data = await makeHttpCall(options);
        
        if(data.url === undefined)
        {
            alert(`Player ${playerId} not fount`);
            return;
        }
        let documentData = {
            date: "",
            MimeType: "",
            src: "",         
            uploadId:"",
        }
        documentData.src = data.url === undefined ? "" : data.url;
        documentData.date = data.createdAt === undefined ? "" : data.createdAt;
        documentData.MimeType = data.documentType === undefined ? "" : data.documentType;
        documentData.uploadId = data.uploadId === undefined ? "" : data.uploadId;
        setDocumentData(documentData);
              
    } 
    catch(e){
        alert(e);
    }
}

export const deleteImage = (uploadId) => {

    if(uploadId === "")
    {
        alert('Upload id is empty');
        return;
    }
    const options =
    {
        method: 'delete',
        url: process.env.REACT_APP_BACK_END_GET_DOCUMENT_IMAGE+'/document/'+ uploadId
    }

    try
    { 
       alert(makeHttpCall(options).message);
    }catch(e){
        alert(e);
    }

}
