                        this.successMessage = 'No Results Matching the Criteria have been Found.';
                        this.errorMessage = null;
                        //this.showResetPasswordModal = false;
                        //this.showTicketHistoryToast = true;
                        this.showToast = true;
                    }
                }
            })
            .catch((error) => {
                this.errorMessage = error.toString();
            });
