    try
    {
        
        const data = await makeHttpCall(options);
        
        if(data.message !== undefined)
        {
            alert(`Player ${playerId} not fount`);
            return [];
        }  
        console.log(">>>>>>>>>>>>>>> RETURNING Data", data);      
        return data.documents;              
    } 
    catch(e){
        alert(e);
    }

}
